package icehs.science.chapter04;

public class InDeOperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 20;
		
		System.out.println(++number);
		System.out.println(number++);
		System.out.println(--number);
		System.out.println(number--);
		System.out.println(number);
	}

}
